// App.js
import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001');

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [username, setUsername] = useState('');
  const [channel, setChannel] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Set a default username for testing
    const user = prompt('Enter your username');
    setUsername(user);

    // Listen for initial messages when the component mounts
    socket.emit('initial-messages', channel);

    // Listen for incoming messages from the server
    socket.on('message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    // Listen for initial messages when joining a new channel
    socket.on('initial-messages', (data) => {
      if (data) {
        setMessages(data);
      }
    });

    // Clean up the socket connection when the component unmounts
    return () => {
      socket.disconnect();
    };
  }, [channel]);

  // Function to fetch messages from the server
  const fetchMessages = async () => {
    try {
      const response = await fetch(`http://localhost:3001/messages?channel=${channel}`);
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  // Function to send a message
  const sendMessage = () => {
    const newMessage = { text: input, timestamp: new Date(), from: username, channel };
    socket.emit('message', newMessage);
    setMessages((prevMessages) => [...prevMessages, newMessage]);
    setInput('');
  };

  // Function to join a channel
  const joinChannel = (newChannel) => {
    socket.emit('join-channel', { username, channelName: newChannel });
    setChannel(newChannel);
    setInput('');
    fetchMessages(); // Fetch messages for the new channel
  };

  // Scroll to the bottom when new messages are received
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  return (
    <div className="app-container">
      <div className="chat-container">
        <div className="channel-container">
          <h2>Channels</h2>
          <button onClick={() => joinChannel('general')}>General</button>
          <button onClick={() => joinChannel('random')}>Random</button>
          {/* Add more channels as needed */}
        </div>
        <div className="message-container">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.from === 'Admin' ? 'admin' : ''}`}>
              <p className="message-text">
                <strong>{message.from}:</strong> {message.text}
              </p>
              <span className="message-timestamp">
                {new Date(message.timestamp).toLocaleTimeString()}
              </span>
            </div>
          ))}
          <div ref={messagesEndRef}></div>
        </div>
        <div className="input-container">
          <input
            type="text"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default App;
