import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001');

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Fetch messages from the server when the component mounts
    fetchMessages();

    // Listen for incoming messages from the server
    socket.on('message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    // // Clean up the socket connection when the component unmounts
    return () => {
      socket.disconnect();
    };
  }, [messages]);


  // Function to fetch messages from the server
//   const fetchMessages = async () => {
//     try {
//       const response = await fetch('http://localhost:3001/messages');
//       const data = await response.json();
// console.log('✌️data --->', data);
//       setMessages(data);
//     } catch (error) {
//       console.error('Error fetching messages:', error);
//     }
//   };
const fetchMessages = async () => {
  try {
    const response = await fetch('http://localhost:3001/messages');
    const data = await response.json();

    // Only set messages if there are new messages
    if (data.length > messages.length) {
      setMessages(data);
    }
  } catch (error) {
    console.error('Error fetching messages:', error);
  }
};

  useEffect(() => {
    fetchMessages();

    socket.on('message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    // Remove the cleanup code
  }, [messages]);

  // useEffect(() => {
  //   // Scroll to the bottom when new messages are received
  //   if (messagesEndRef.current) {
  //     messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
  //   }
  // }, [messages]);

  const sendMessage = () => {
    if(input != ""){

      // Emit a message to the server
      const newMessage = { text: input, timestamp: new Date(), fromMe: true };
      console.log('Sending message:', newMessage); // Log the message before sending
      socket.emit('message', newMessage);

      setMessages((prevMessages) => [...prevMessages, newMessage]);
      setInput('');
    }
  };

  return (
    <div className="app-container">
      <div className="chat-container">
        <div className="message-container">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.fromMe ? 'from-me' : ''}`}>
              <p className="message-text">{message.text}</p>
              <span className="message-timestamp">{new Date(message.timestamp).toLocaleTimeString()}</span>
            </div>
          ))}
          <div ref={messagesEndRef}></div>
        </div>
        <div className="input-container">
          <input type="text" value={input} onChange={(e) => setInput(e.target.value)} />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default App;
