// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001');

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [username, setUsername] = useState('');
  const [channelId, setChannelId] = useState('');
  const [channels, setChannels] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    setUsername(prompt('Enter your username:'));
    fetchChannels();
  }, []);

  useEffect(() => {
    if (channelId) {
      socket.emit('join-channel', { username, channelId });
      fetchMessages();
    }
  }, [channelId, username]);

  socket.on('user-joined', ({ username: joinedUsername, channelId: joinedChannelId }) => {
    if (joinedChannelId === channelId) {
      setMessages((prevMessages) => [
        ...prevMessages,
        { text: `${joinedUsername} joined the channel.`, system: true },
      ]);
    }
  });

  socket.on('initial-messages', (initialMessages) => {
    setMessages(initialMessages);
  });

  socket.on('message', (newMessage) => {
    setMessages((prevMessages) => [...prevMessages, newMessage]);
  });

  const fetchChannels = async () => {
    try {
      const response = await fetch('http://localhost:3001/channels');
      const data = await response.json();
      setChannels(data);
    } catch (error) {
      console.error('Error fetching channels:', error);
    }
  };

  const fetchMessages = async () => {
    try {
      const response = await fetch(`http://localhost:3001/messages?channel=${channelId}`);
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const sendMessage = () => {
    if (input.trim() === '') return;

    socket.emit('message', { username, channelId, text: input });
    setInput('');
  };

  const joinChannel = (newChannelId) => {
    setChannelId(newChannelId);
  };

  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  return (
    <div className="app-container">
      <div className="chat-container">
        <div className="channel-container">
          <h2>Channels</h2>
          {channels.map((channel) => (
            <button key={channel._id} onClick={() => joinChannel(channel._id)}>
              {channel._id}
            </button>
          ))}
        </div>
        <div className="message-container">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.system ? 'system-message' : ''}`}>
              <p className="message-text">{message.text}</p>
              <span className="message-timestamp">
                {new Date(message.createdAt).toLocaleTimeString()}
              </span>
            </div>
          ))}
          <div ref={messagesEndRef}></div>
        </div>
        <div className="input-container">
          <input
            type="text"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default App;
