// src/components/VideoCall.js
import React, { useEffect, useRef, useState } from 'react';
import { Paper, Button, TextField, List, ListItem, ListItemText } from '@mui/material';
import io from 'socket.io-client';

const VideoCall = () => {
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const commentInputRef = useRef();

  const [participants, setParticipants] = useState([]);
  const [comments, setComments] = useState([]);
  const [isMuted, setIsMuted] = useState(false);
  const [isVideoOn, setIsVideoOn] = useState(true);

  useEffect(() => {
    const socket = io('http://localhost:3001'); // Replace with your server address
    const roomId = 'vedio call'; // You can generate this dynamically or hardcode it

    const username = prompt('Enter your username:'); // You can replace this with your authentication logic

    socket.emit('join-room', roomId, username);

    socket.on('user-joined', ({ username, userId }) => {
      setParticipants((prevParticipants) => [...prevParticipants, { username, userId }]);
    });

    socket.on('user-left', ({ username, userId }) => {
      setParticipants((prevParticipants) =>
        prevParticipants.filter((participant) => participant.userId !== userId)
      );
    });

    socket.on('receive-comment', ({ username, comment }) => {
      setComments((prevComments) => [...prevComments, { username, comment }]);
    });

    socket.on('toggled-mute', () => {
      setIsMuted((prev) => !prev);
    });

    socket.on('toggled-video', () => {
      setIsVideoOn((prev) => !prev);
    });

    // Fetch media devices and start the video call

    return () => {
      // Clean up logic if needed
    };
  }, []);

  const handleSendComment = () => {
    const comment = commentInputRef.current.value;
    if (comment.trim() !== '') {
      // Send the comment to the server
      const socket = io('http://localhost:3001'); // Replace with your server address
      const roomId = 'vedio call'; // You can generate this dynamically or hardcode it

      socket.emit('send-comment', { roomId, comment });
      commentInputRef.current.value = ''; // Clear the input field
    }
  };

  const handleToggleMute = () => {
    // Send a message to the server to toggle mute
    const socket = io('http://localhost:3001'); // Replace with your server address
    const roomId = 'vedio call'; // You can generate this dynamically or hardcode it

    socket.emit('toggle-mute', { roomId });
  };

  const handleToggleVideo = () => {
    // Send a message to the server to toggle video
    const socket = io('http://localhost:3001'); // Replace with your server address
    const roomId = 'your-room-id'; // You can generate this dynamically or hardcode it

    socket.emit('toggle-video', { roomId });
  };

  return (
    <Paper elevation={3}>
      <div>
        <video ref={localVideoRef} autoPlay muted={isMuted} playsInline></video>
        <video ref={remoteVideoRef} autoPlay playsInline></video>
      </div>
      <div>
        <Button onClick={handleToggleMute}>{isMuted ? 'Unmute' : 'Mute'}</Button>
        <Button onClick={handleToggleVideo}>{isVideoOn ? 'Turn off Video' : 'Turn on Video'}</Button>
      </div>
      <div>
        <TextField inputRef={commentInputRef} label="Type your comment" />
        <Button onClick={handleSendComment}>Send Comment</Button>
      </div>
      <div>
        <List>
          {participants.map((participant) => (
            <ListItem key={participant.userId}>
              <ListItemText primary={participant.username} />
            </ListItem>
          ))}
        </List>
      </div>
      <div>
        <List>
          {comments.map((comment, index) => (
            <ListItem key={index}>
              <ListItemText primary={`${comment.username}: ${comment.comment}`} />
            </ListItem>
          ))}
        </List>
      </div>
    </Paper>
  );
};

export default VideoCall;
