import React, { useEffect, useRef, useState } from 'react';
import {
  Paper,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  Typography,
  Switch,
  Box,
} from '@mui/material';
import io from 'socket.io-client';


function App() {
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const [roomId, setRoomId] = useState('Tellignet');
  const [username, setUsername] = useState('');
  const [participants, setParticipants] = useState([]);
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [isAudioMuted, setIsAudioMuted] = useState(false);
  const [isVideoMuted, setIsVideoMuted] = useState(true);
  const [hideField, setHideField] = useState(false);
  const [stream, setStream] = useState(null);
  const socket = io('http://localhost:3001'); // Replace with your server address

  useEffect(() => {

  const getMedia = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(stream);
      localVideoRef.current.srcObject = stream;
    } catch (error) {
      console.error('Error accessing media devices:', error);
    }
  };

  getMedia();

  return () => {
    // Clean up logic if needed
  };
}, []);

const joinRoom = () => {
  setHideField(true);
  socket.emit('join-room', roomId, username);
};

const dropCall = () => {
  socket.disconnect();
};

const toggleAudio = () => {
  socket.emit('toggle-audio', roomId);
  setIsAudioMuted(!isAudioMuted);
};

const toggleVideo = () => {
  socket.emit('toggle-video', roomId);
  const videoTracks = stream.getVideoTracks();
  videoTracks.forEach((track) => (track.enabled = !isVideoMuted));
  setIsVideoMuted(!isVideoMuted);
};

const sendComment = () => {
  socket.emit('send-comment', roomId, commentInput, username);
  setCommentInput('');
};

socket.on('user-joined', (user) => {
  // Handle user joined
  console.log(`${user} joined the call`);
});

socket.on('user-left', (user) => {
  // Handle user left
  console.log(`${user} left the call`);
});

socket.on('update-participants', (newParticipants) => {
  setParticipants(newParticipants);
});

socket.on('toggle-audio', (userId) => {
  // Handle toggle audio
  console.log(`${userId} toggled audio`);
});

socket.on('toggle-video', (userId) => {
  // Handle toggle video
  console.log(`${userId} toggled video`);
});

socket.on('receive-comment', (data) => {
  console.log('✌️data --->', data);
  setComments((prevComments) => [...prevComments, data]);
});



  console.log('✌️peers --->', peers);
  return (

    // <VideoCall/>
    <Paper
      elevation={3}
      sx={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}
    >
      <div
        style={{
          position: 'relative',
          marginBottom: '20px',
        }}
      >
        <div style={{ width: '600px', height: '500px', display: 'flex' }}>
          <video
            ref={localVideoRef}
            autoPlay
            muted={isAudioMuted}
            playsInline
            style={{ flex: 1 }}
          ></video>
          <video
            ref={remoteVideoRef}
            autoPlay
            playsInline
            style={{ flex: 1 }}
          ></video>
        </div>
        <div
          style={{
            position: 'absolute',
            bottom: 0,
            left: '50%',
            transform: 'translateX(-50%)',
            display: 'flex',
            justifyContent: 'center',
            marginBottom: '10px',
          }}
        >
          <Button
            variant="contained"
            color={isAudioMuted ? 'default' : 'primary'}
            onClick={toggleAudio}
            style={{ marginRight: '10px' }}
          >
            {isAudioMuted ? 'Unmute Audio' : 'Mute Audio'}
          </Button>
          <Button
            variant="contained"
            color={isVideoMuted ? 'default' : 'primary'}
            onClick={toggleVideo}
          >
            {isVideoMuted ? 'Turn On Video' : 'Turn Off Video'}
          </Button>
        </div>
      </div>
      <div style={{ padding: '10px' }}>
        {hideField !== '' ? (
          <p style={{ margin: '10px', fontSize: '20px', color: 'green' }}>
            {username} joined the call
          </p>
        ) : (
          <>
            <TextField
              sx={{ margin: '10px' }}
              label="Username"
              variant="outlined"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <Button
              sx={{ padding: '15px' }}
              variant="contained"
              color="primary"
              onClick={joinRoom}
            >
              Join Call
            </Button>
          </>
        )}
      </div>
      <div>
        <div
          style={{
            display: 'flex',
            flexDirection: 'row',
            alignItems: 'center',
            margin: '10px',
          }}
        >
          {/* Existing toggle buttons */}
        </div>
      </div>
      <div>
        <Typography variant="h6">Participants:</Typography>
        <List>
          {participants.map((participant, index) => (
            <ListItem key={index}>
              <ListItemText primary={participant} />
            </ListItem>
          ))}
        </List>
      </div>

      <Box mt={2}>
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            marginBottom: '10px',
          }}
        >
          <Typography variant="h6" style={{ marginRight: '10px' }}>
            Type a comment:
          </Typography>
          <TextField
            sx={{ width: '400px' }}
            label="Type a comment"
            variant="outlined"
            value={commentInput}
            onChange={(e) => setCommentInput(e.target.value)}
          />
        </div>
        <Button
          sx={{ padding: '15px' }}
          variant="contained"
          color="primary"
          onClick={sendComment}
        >
          Send Comment
        </Button>
      </Box>
      <div>
        <Typography variant="h6">Comments:</Typography>
        <List>
          {comments.map((comment, index) => (
            <ListItem key={index}>
              <ListItemText primary={`${comment.username}: ${comment.comment}`} />
            </ListItem>
          ))}
        </List>
      </div>
    </Paper>


  );
}

export default App;
