// src/components/VideoCall.js
import React, { useEffect, useRef, useState } from 'react';
import {
  Paper,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  Typography,
  Switch
} from '@mui/material';
import io from 'socket.io-client';

const VideoCall = () => {
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const [roomId, setRoomId] = useState('Tellignet');
  const [username, setUsername] = useState('');
  const [participants, setParticipants] = useState([]);
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [isAudioMuted, setIsAudioMuted] = useState(false);
  const [isVideoMuted, setIsVideoMuted] = useState(true);
  const [hideField , setHideField] = useState(false) 
  const [stream, setStream] = useState(null);
  const socket = io('http://localhost:3001'); // Replace with your server address

  useEffect(() => {
    const getMedia = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setStream(stream);
        localVideoRef.current.srcObject = stream;
      } catch (error) {
        console.error('Error accessing media devices:', error);
      }
    };

    getMedia();

    return () => {
      // Clean up logic if needed
    };
  }, []);

  const joinRoom = () => {
    setHideField(true)
    socket.emit('join-room', roomId, username);
  };

  const dropCall = () => {
    socket.disconnect();
  };

  const toggleAudio = () => {
    socket.emit('toggle-audio', roomId);
    setIsAudioMuted(!isAudioMuted);
  };

  const toggleVideo = () => {
    socket.emit('toggle-video', roomId);
    const videoTracks = stream.getVideoTracks();
    videoTracks.forEach((track) => (track.enabled = !isVideoMuted));
    setIsVideoMuted(!isVideoMuted);
  };

  const sendComment = () => {
    socket.emit('send-comment', roomId, commentInput, username);
    setCommentInput('');
  };

  socket.on('user-joined', (user) => {
    // Handle user joined
    console.log(`${user} joined the call`);
  });

  socket.on('user-left', (user) => {
    // Handle user left
    console.log(`${user} left the call`);
  });

  socket.on('update-participants', (newParticipants) => {
    setParticipants(newParticipants);
  });

  socket.on('toggle-audio', (userId) => {
    // Handle toggle audio
    console.log(`${userId} toggled audio`);
  });

  socket.on('toggle-video', (userId) => {
    // Handle toggle video
    console.log(`${userId} toggled video`);
  });

  socket.on('receive-comment', (data) => {
    console.log('✌️data --->', data);
    setComments((prevComments) => [...prevComments, data]);
  });

  console.log('✌️comments --->', comments);
  return (
    <Paper elevation={3} sx={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
      <div style={{ width: "600px", height: "500px" }}>
        <video ref={localVideoRef} autoPlay muted={isAudioMuted} playsInline></video>
        <video ref={remoteVideoRef} autoPlay playsInline></video>
      </div>
      <div style={{ padding: "10px" }}>
        {/* <TextField
          sx={{margin:"10px"}}
          label="Room ID"
          variant="outlined"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        /> */}
        {hideField != "" ? (
          <p style={{margin:"10px",fontSize:"20px",color:"green"}}>

            {username} joint the call`
          </p>

        ) : (
          <>
            <TextField
              sx={{ margin: "10px" }}
              label="Username"
              variant="outlined"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <Button sx={{ margin: "10px", padding: "12px" }} variant="contained" color="primary" onClick={joinRoom}>
              Join Call
            </Button>
          </>
        )}
        {/* <Button    sx={{margin:"10px", padding:"10px"}}variant="contained" color="secondary" onClick={dropCall}>
          Drop Call
        </Button> */}
      </div>
      <div>
        <Button variant="contained" color="secondary" onClick={toggleAudio}>
          {isAudioMuted ? 'Unmute Audio' : 'Mute Audio'}
        </Button>
        {/* <Button variant="contained" color="secondary" onClick={toggleVideo}>
          {isVideoMuted ? 'Video' : 'Video'}
        </Button> */}
        <div style={{ display: "flex", flexDirection: "row", alignItems: "center" }}>

          <Typography variant="h6">{isVideoMuted ? "Turn Off Video" : "Turn On Video"}</Typography>
          <Switch onClick={toggleVideo} checked={!isVideoMuted} />
        </div>
      </div>
      <div>
        <TextField
          label="Type a comment"
          variant="outlined"
          value={commentInput}
          onChange={(e) => setCommentInput(e.target.value)}
        />
        <Button sx={{ padding: "15px" }} variant="contained" color="primary" onClick={sendComment}>
          Send Comment
        </Button>
      </div>
      <div>
        <Typography variant="h6">Participants:</Typography>
        <List>
          {participants.map((participant, index) => (
            <ListItem key={index}>
              <ListItemText primary={participant} />
            </ListItem>
          ))}
        </List>
      </div>
      <div>
        <Typography variant="h6">Comments:</Typography>
        <List>
          {comments.map((comment, index) => (
            <ListItem key={index}>
              <ListItemText primary={`${comment.username}: ${comment.comment}`} />
            </ListItem>
          ))}
        </List>
      </div>
    </Paper>
  );
};

export default VideoCall;
