// App.js
import React, { useEffect, useState, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';
import { Button, Container, Grid, Paper, Typography, Slider, Switch } from '@mui/material';
import VideoCall from './VideoCall';
const socket = io.connect('http://localhost:3001');

function App() {
  const [stream, setStream] = useState(null);
  const [myPeer, setMyPeer] = useState(null);
  const [peers, setPeers] = useState([]);
  const [audioMuted, setAudioMuted] = useState(false);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const [targetSocketId, setTargetSocketId] = useState(null); // Added state for targetSocketId

  const myVideoRef = useRef();
useEffect(() => {
  navigator.mediaDevices.getUserMedia({ video: true, audio: true })
    .then((userStream) => {
      setStream(userStream);
      myVideoRef.current.srcObject = userStream;

      socket.on('your-id', (userId) => {
        console.log('Your ID:', userId);
        setMyPeer(new Peer({ initiator: true, trickle: false, stream: userStream }));
      });

      socket.on('user-connected', (userId) => {
        console.log('User Connected:', userId);
        connectToNewUser(userId, userStream);
      });
    });

  return () => {
    if (myPeer) myPeer.destroy();
  };
}, []);

  const connectToNewUser = (userId, userStream) => {
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream: userStream,
    });

    setPeers((prevPeers) => [...prevPeers, { peerId: userId, peer }]);

    peer.on('signal', (data) => {
      setTargetSocketId(userId);
      socket.emit('send-offer', { signalData: data, to: userId });
    });

    peer.on('stream', (stream) => {
      setPeers((prevPeers) =>
        prevPeers.map((p) =>
          p.peerId === userId ? { ...p, peer: { ...p.peer, stream } } : p
        )
      );
    });

    socket.on('answer-call', (data) => {
      const existingPeer = peers.find((p) => p.peerId === userId);
      if (existingPeer) {
        existingPeer.peer.signal(data.signal);
      }
    });
  };



  const answerCall = () => {
    const peer = new Peer({ initiator: false, trickle: false, stream });

    peer.on('signal', (data) => {
      socket.emit('send-answer', { signalData: data, to: targetSocketId });
    });
console.log('✌️targetSocketId --->', targetSocketId);
    socket.on('receive-offer', (data) => {
      if (data.from === targetSocketId) {
        const existingPeer = peers.find((p) => p.peerId === targetSocketId);
        if (existingPeer) {
          existingPeer.peer.signal(data.signal);
        }
      }
    });

    setPeers((prevPeers) => [...prevPeers, { peerId: targetSocketId, peer }]);
  };


  const toggleAudio = () => {
    const audioTracks = stream.getAudioTracks();
    audioTracks.forEach((track) => (track.enabled = !audioMuted));
    setAudioMuted(!audioMuted);
  };

  const toggleVideo = () => {
    const videoTracks = stream.getVideoTracks();
    videoTracks.forEach((track) => (track.enabled = !videoEnabled));
    setVideoEnabled(!videoEnabled);
  };


console.log('✌️peers --->', peers);
  return (
    <Container>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper elevation={3} style={{ padding: '20px', textAlign: 'center' }}>
            <Typography variant="h5">Video Chat App</Typography>
          </Paper>
        </Grid>
        <VideoCall/>
        {/* <Grid item xs={12} md={8}>
