// src/components/VideoCall.js
import React, { useEffect, useRef, useState } from 'react';
import {
  Paper,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  Typography,
  Switch, Box
} from '@mui/material';
import io from 'socket.io-client';
import "./App.css"
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Toolbar, IconButton, Grid } from '@material-ui/core';
import VideocamIcon from '@material-ui/icons/Videocam';
import MicIcon from '@material-ui/icons/Mic';
import MicOffIcon from '@material-ui/icons/MicOff';
import VideocamOffIcon from '@material-ui/icons/VideocamOff';
import PeopleIcon from '@material-ui/icons/People';
const VideoCall = () => {
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const [roomId, setRoomId] = useState('Tellignet');
  const [username, setUsername] = useState('');
  const [participants, setParticipants] = useState([]);
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [isAudioMuted, setIsAudioMinuted] = useState(false);
  const [isVideoMuted, setIsVideoMuted] = useState(true);
  const [hideField, setHideField] = useState(false)
  const [stream, setStream] = useState(null);
  const socket = io('http://localhost:3001'); // Replace with your server address

  useEffect(() => {
    const getMedia = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
        setStream(stream);
        localVideoRef.current.srcObject = stream;
      } catch (error) {
        console.error('Error accessing media devices:', error);
      }
    };

    getMedia();

    return () => {
      // Clean up logic if needed
    };
  }, []);

  const joinRoom = () => {
    setHideField(true)
    socket.emit('join-room', roomId, username);
  };

  const dropCall = () => {
    socket.disconnect();
  };

  const toggleAudio = () => {
    socket.emit('toggle-audio', roomId);
    setIsAudioMinuted(!isAudioMuted);
  };

  const toggleVideo = () => {
    socket.emit('toggle-video', roomId);
    const videoTracks = stream.getVideoTracks();
    videoTracks.forEach((track) => (track.enabled = !isVideoMuted));
    setIsVideoMuted(!isVideoMuted);
  };

  const sendComment = () => {
    socket.emit('send-comment', roomId, commentInput, username);
    setCommentInput('');
  };

  socket.on('user-joined', (user) => {
    // Handle user joined
    console.log(`${user} joined the call`);
  });

  socket.on('user-left', (user) => {
    // Handle user left
    console.log(`${user} left the call`);
  });

  socket.on('update-participants', (newParticipants) => {
    setParticipants(newParticipants);
  });

  socket.on('toggle-audio', (userId) => {
    // Handle toggle audio
    console.log(`${userId} toggled audio`);
  });

  socket.on('toggle-video', (userId) => {
    // Handle toggle video
    console.log(`${userId} toggled video`);
  });

  socket.on('receive-comment', (data) => {
    console.log('✌️data --->', data);
    setComments((prevComments) => [...prevComments, data]);
  });

  console.log('✌️comments --->', comments);
  return (
    <>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar>
          <Typography variant="h6" className={classes.title}>
            Meet At Voli
          </Typography>
          <div className={classes.centerIcons}>
            <IconButton color="inherit" onClick={toggleAudio} className={classes.iconButton}>
              {isAudioMuted ? <MicOffIcon /> : <MicIcon />}
            </IconButton>
            <IconButton color="inherit" onClick={toggleVideo} className={classes.iconButton}>
              {isVideoMuted ? <VideocamOffIcon /> : <VideocamIcon />}
            </IconButton>
            <IconButton color="inherit" onClick={toggleMessages} className={classes.iconButton}>
              <ChatIcon />
            </IconButton>
            <IconButton color="inherit" onClick={toggleParticipants} className={classes.iconButton}>
              <PeopleIcon />
            </IconButton>
          </div>
        </Toolbar>
      </AppBar>

      <Box style={{
        display: "flex",
        margin: "5px",
        display: "flex",
        justifyContent: "center",
        flexDirection: "column",
        alignItems: "center",
      }} elevation={3}>
        <div className="video-container">
          <video ref={localVideoRef} autoPlay muted={isAudioMuted} playsInline className="local-video"></video>
          <video ref={remoteVideoRef} autoPlay playsInline className="remote-video"></video>
        </div>


        <div style={{ padding: "10px" }}>
          {/* <TextField
          sx={{ margin: "10px" }}
          label="Room ID"
          variant="outlined"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        /> */}
          {hideField != "" ? (
            <p style={{ margin: "10px", fontSize: "20px", color: "green" }}>

              {username}  has joined the call
            </p>

          ) : (
            <>
              <TextField
                sx={{}}
                label="Username"
                variant="outlined"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <Button sx={{ padding: "15px" }} variant="contained" color="primary" onClick={joinRoom}>
                Join Call
              </Button>
            </>
          )}
          {/* <Button    sx={{margin:"10px", padding:"10px"}}variant="contained" color="secondary" onClick={dropCall}>
          Drop Call
        </Button> */}
        </div>
        <div>
          <div style={{ display: "flex", flexDirection: "row", alignItems: "center", margin: "10px" }}>


            <Typography variant="h6">
              {isAudioMuted ? 'Unmute Audio' : 'Mute Audio'}

            </Typography>
            <Switch onClick={toggleAudio} checked={isAudioMuted} />


            <Typography variant="h6">{isVideoMuted ? "Turn Off Video" : "Turn On Video"}</Typography>
            <Switch onClick={toggleVideo} checked={!isVideoMuted} />
          </div>
        </div>
        <div>
          <Typography variant="h6">Participants:</Typography>
          <List>
            {participants.map((participant, index) => (
              <ListItem key={index}>
                <ListItemText primary={participant} />
              </ListItem>
            ))}
          </List>
        </div>

        <Box mt={2}>
          <TextField sx={{ width: "400px" }}
            label="Type a comment"
            variant="outlined"
            value={commentInput}
            onChange={(e) => setCommentInput(e.target.value)}
          />
          <Button sx={{ padding: "15px" }} variant="contained" color="primary" onClick={sendComment}>
            Send Comment
          </Button>
        </Box>

        <div>
          <Typography variant="h6">Comments:</Typography>
          <List>
            {comments.map((comment, index) => (
              <ListItem key={index}>
                <ListItemText primary={`${comment.username}: ${comment.comment}`} />
              </ListItem>
            ))}
          </List>
        </div>
      </Box>
    </>

  );
};

export default VideoCall;
