import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

import './App.css'; // Import the CSS file
const socket = io('http://localhost:3001');

function App() {
  const [username, setUsername] = useState('');
  const [channel, setChannel] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [joinedChannel, setJoinedChannel] = useState('');

  useEffect(() => {
    // Set up listeners for incoming messages and user joins
    socket.on('message', ({ user, text }) => {
      if (user !== 'System') {
        setMessages((prevMessages) => {
          const isDuplicate = prevMessages.some((msg) => msg.text === text && msg.user === user);
          return isDuplicate ? prevMessages : [...prevMessages, { user, text }];
        });
      }
    });
    socket.on('userJoin', ({ username, channel }) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { user: 'System', text: `${username} joined ${channel}` },
      ]);

      // Store the currently joined channel for displaying the message
      setJoinedChannel(channel);
    });

    // Clean up the socket connection when the component unmounts
    // return () => {
    //   socket.disconnect();
    // };
  }, []);

  const handleJoin = () => {
    socket.emit('join', { username, channel }); // Pass the channel information
  };

  const handleCreateChannel = () => {
    socket.emit('createChannel', channel);
  }

  const handleMessage = () => {
    socket.emit('message', { user: username, channel, text: message });
    setMessage('');
  };

  return (
    <div className="app-container">
      <div className="join-container">
        <input
          type="text"
          placeholder="Username"
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="text"
          placeholder="Channel"
          onChange={(e) => setChannel(e.target.value)}
        />
        <button onClick={handleJoin}>Join</button>
        <button onClick={handleCreateChannel}>Create Channel</button>
      </div>

      <div className="chat-container">
        <div className="message-container">
          {messages.map((msg, index) => (
            <div key={index} className="message">
              {msg.username}: {msg.text}
            </div>
          ))}
        </div>

        <div className="input-container">
          <input
            type="text"
            placeholder="Type your message"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
          />
          <button onClick={handleMessage}>Send</button>
        </div>
      </div>

      {joinedChannel && (
        <div className="join-message">
          You have joined the channel: <span className="channel-name">{joinedChannel}</span>
        </div>
      )}
    </div>
  );
}

export default App;
