import React, { useEffect, useRef, useState } from 'react';
import {
  Paper,
  Button,
  TextField,
  List,
  ListItem,
  ListItemText,
  Container,
  Switch,
  Box,
  Drawer
} from '@mui/material';
import io from 'socket.io-client';

import VideoCall from "./VideoCall"
import { makeStyles } from '@material-ui/core/styles';
import { AppBar, Toolbar, IconButton, Typography, Grid } from '@material-ui/core';
import VideocamIcon from '@material-ui/icons/Videocam';
import MicIcon from '@material-ui/icons/Mic';
import MicOffIcon from '@material-ui/icons/MicOff';
import VideocamOffIcon from '@material-ui/icons/VideocamOff';
import PeopleIcon from '@material-ui/icons/People';
import ChatIcon from '@material-ui/icons/Chat';
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    height: '100vh', // 100% of viewport height
    overflow: 'hidden',
  },
  appBar: {
    backgroundColor: '#333',
    display: 'flex',
    justifyContent: 'space-between',
  },
  title: {
    flexGrow: 1,
  },
  centerIcons: {
    display: 'flex',
    alignItems: 'center',
  },
  iconButton: {
    margin: theme.spacing(2),
  },
  videoContainer: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: 'calc(100vh - 64px)',
    width: '100%', // Make it cover the full width
    border: 'solid',

  },
  video: {
    width: '100%',
    height: '100%',
  },
  drawer: {
    width: '300px',
  },
  drawerPaper: {
    width: '300px',
  },
  drawerList: {
    padding: theme.spacing(2),
  },
}));
function App() {
  const classes = useStyles();
  const [isMicMuted, setIsMicMuted] = useState(false);
  const [isVideoOff, setIsVideoOff] = useState(false);

  const toggleMic = () => {
    setIsMicMuted(!isMicMuted);
  };



  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const [showMessages, setShowMessages] = useState(false);
  const [showParticipants, setShowParticipants] = useState(false);

  const [roomId, setRoomId] = useState('Tellignet');
  const [username, setUsername] = useState('');
  const [participants, setParticipants] = useState([]);
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [isAudioMuted, setIsAudioMuted] = useState(false);
  const [isVideoMuted, setIsVideoMuted] = useState(true);
  const [hideField, setHideField] = useState(false);
  const [stream, setStream] = useState(null);
  const socket = io('http://localhost:3001'); // Replace with your server address

  useEffect(() => {

    const getMedia = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({
          video: true,
          audio: true,
        });
        setStream(stream);
        localVideoRef.current.srcObject = stream;
      } catch (error) {
        console.error('Error accessing media devices:', error);
      }
    };

    getMedia();

    return () => {
      // Clean up logic if needed
    };
  }, []);

  const joinRoom = () => {
    setHideField(true);
    socket.emit('join-room', roomId, username);
  };

  const dropCall = () => {
    socket.disconnect();
  };

  const toggleAudio = () => {
    socket.emit('toggle-audio', roomId);
    setIsAudioMuted(!isAudioMuted);
  };

  const toggleVideo = () => {
    socket.emit('toggle-video', roomId);
    const videoTracks = stream.getVideoTracks();
    videoTracks.forEach((track) => (track.enabled = !isVideoMuted));
    setIsVideoMuted(!isVideoMuted);
  };

  const sendComment = () => {
    socket.emit('send-comment', roomId, commentInput, username);
    setCommentInput('');
  };

  socket.on('user-joined', (user) => {
    // Handle user joined
    console.log(`${user} joined the call`);
  });

  socket.on('user-left', (user) => {
    // Handle user left
    console.log(`${user} left the call`);
  });

  socket.on('update-participants', (newParticipants) => {
    setParticipants(newParticipants);
  });

  socket.on('toggle-audio', (userId) => {
    // Handle toggle audio
    console.log(`${userId} toggled audio`);
  });

  socket.on('toggle-video', (userId) => {
    // Handle toggle video
    console.log(`${userId} toggled video`);
  });

  socket.on('receive-comment', (data) => {
    console.log('✌️data --->', data);
    setComments((prevComments) => [...prevComments, data]);
  });


  const toggleMessages = () => {
    setShowMessages(!showMessages);
  };

  const toggleParticipants = () => {
    setShowParticipants(!showParticipants);
  };

  return (

    // <VideoCall/>
    <div className={classes.root}>
    <AppBar position="static" className={classes.appBar}>
      <Toolbar>
        <Typography variant="h6" className={classes.title}>
          Meet At Voli
        </Typography>
        <div className={classes.centerIcons}>
          <IconButton color="inherit" onClick={toggleMic} className={classes.iconButton}>
            {isMicMuted ? <MicOffIcon /> : <MicIcon />}
          </IconButton>
          <IconButton color="inherit" onClick={toggleVideo} className={classes.iconButton}>
            {isVideoOff ? <VideocamOffIcon /> : <VideocamIcon />}
          </IconButton>
          <IconButton color="inherit" onClick={toggleMessages} className={classes.iconButton}>
            <ChatIcon />
          </IconButton>
          <IconButton color="inherit" onClick={toggleParticipants} className={classes.iconButton}>
            <PeopleIcon />
          </IconButton>
        </div>
      </Toolbar>
    </AppBar>

    <Container className={classes.videoContainer}>
      <video ref={localVideoRef} autoPlay muted={isAudioMuted} className={classes.video} playsInline ></video>
      <video ref={remoteVideoRef} autoPlay className={classes.video} playsInline ></video>
    </Container>

    <Drawer
        anchor="right"
        open={showMessages}
        onClose={toggleMessages}
        classes={{ paper: classes.drawerPaper }}
      >
        <List className={classes.drawerList}>
          <div className={classes.commentsContainer}>
            <Typography variant="h6">Comments</Typography>
            <List>
              {comments.map((comment, index) => (
                <ListItem key={index}>
                  <ListItemText>{comment}</ListItemText>
                </ListItem>
              ))}
            </List>
            <TextField
              label="Add a comment"
              variant="outlined"
              fullWidth
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              className={classes.commentInput}
            />
            <Button variant="contained" color="primary" onClick={sendComment}>
              Add Comment
            </Button>
          </div>
        </List>
      </Drawer>

    <Drawer
      anchor="right"
      open={showParticipants}
      onClose={toggleParticipants}
      classes={{ paper: classes.drawerPaper }}
    >
      <List className={classes.drawerList}>
        {/* Participants content goes here */}
      </List>
    </Drawer>
  </div>



  );
}

export default App;
