import React, { useEffect, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer'; // Import from 'simple-peer' instead of 'peerjs'
import './App.css';

const socket = io('http://localhost:3001');

function App() {
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);

  const peer = new Peer({ initiator: false, trickle: false });

  useEffect(() => {
    navigator.mediaDevices
      .getUserMedia({ video: true, audio: true })
      .then((stream) => {
        localVideoRef.current.srcObject = stream;

        // Send the stream to the peer
        peer.addStream(stream);

        // Set up listeners for WebRTC events
        peer.on('signal', (data) => {
          socket.emit('offer', data);
        });

        socket.on('answer', (data) => {
          peer.signal(data);
        });

        socket.on('ice-candidate', (data) => {
          peer.signal(data);
        });

        // Make an initial connection to the server
        socket.emit('join', { username: 'YourUsername', channel: 'YourChannel' });
      })
      .catch((error) => console.error('Error accessing local media:', error));

    // Set up listeners for WebRTC events
    socket.on('offer', (offer) => {
      peer.signal(offer);
    });

    return () => {
      // Disconnect from the peer and close the connection
      peer.destroy();
    };
  }, []);

  // Set up listeners for WebRTC events
  peer.on('stream', (remoteStream) => {
    remoteVideoRef.current.srcObject = remoteStream;
  });

  return (
    <div className="video-call-container">
      <div className="video-box">
        <video ref={localVideoRef} autoPlay muted className="local-video"></video>
        <div className="local-username">You</div>
      </div>

      <div className="video-box">
        <video ref={remoteVideoRef} autoPlay className="remote-video"></video>
        <div className="remote-username">Remote User</div>
      </div>
    </div>
  );
}

export default App;
