// App.js
import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './App.css';

const socket = io('http://localhost:3001');

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [username, setUsername] = useState('');
  const [channel, setChannel] = useState('');
  const [channels, setChannels] = useState([]);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Set a default username for testing
    setUsername('Admin');

    // Join a default channel for testing
    setChannel('general');

    // Fetch messages from the server when the component mounts
    fetchMessages();

    // Listen for incoming messages from the server
    socket.on('message', (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    // Clean up the socket connection when the component unmounts
    return () => {
      socket.disconnect();
    };
  }, []);

  // Function to fetch messages from the server
  const fetchMessages = async () => {
    try {
      const response = await fetch(`http://localhost:3001/messages?channel=${channel}`);
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  // Function to send a message
  const sendMessage = () => {
    const newMessage = { text: input, timestamp: new Date(), from: username, channel };
    socket.emit('message', newMessage);
    setMessages((prevMessages) => [...prevMessages, newMessage]);
    setInput('');
  };

  const fetchChannels = async () => {
    try {
      const response = await fetch('http://localhost:3001/channels');
      const data = await response.json();
      setChannels(data);
    } catch (error) {
      console.error('Error fetching channels:', error);
    }
  };

  // Function to join a channel
  const joinChannel = async (newChannel) => {
    try {
      const response = await fetch('http://localhost:3001/channels', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name: newChannel }),
      });

      const data = await response.json();
      setChannel(data.name);
      fetchMessages(); // Fetch messages for the new channel
    } catch (error) {
      console.error('Error joining channel:', error);
    }
  };

  // Scroll to the bottom when new messages are received
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  return (
    <div className="app-container">
      <div className="chat-container">
        <div className="channel-container">
          <h2>Channels</h2>
          <button onClick={() => joinChannel('general')}>General</button>
          <button onClick={() => joinChannel('random')}>Random</button>
          {/* Add more channels as needed */}
        </div>
        <div className="message-container">
          {messages.map((message, index) => (
            <div key={index} className="message">
              <p className="message-text">
                <strong>{message.from}:</strong> {message.text}
              </p>
              <span className="message-timestamp">
                {new Date(message.timestamp).toLocaleTimeString()}
              </span>
            </div>
          ))}
          <div ref={messagesEndRef}></div>
        </div>
        <div className="input-container">
          <input
            type="text"
            placeholder="Type your message..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={sendMessage}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default App;
